#pragma config(Hubs,  S1, HTMotor,  HTMotor,  none,     none)
#pragma config(Sensor, S1,     ,               sensorI2CMuxController)
#pragma config(Sensor, S2,     ,               sensorAnalog)
#pragma config(Motor,  mtr_S1_C1_1,     motorBR,       tmotorTetrix, openLoop)
#pragma config(Motor,  mtr_S1_C1_2,     motorFR,       tmotorTetrix, openLoop, reversed)
#pragma config(Motor,  mtr_S1_C2_1,     motorFL,       tmotorTetrix, openLoop, reversed)
#pragma config(Motor,  mtr_S1_C2_2,     motorBL,       tmotorTetrix, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#include "JoystickDriver.c"
#include "drivers\hitechnic-gyro.h"
//#include "rdpartyrobotcdr-3.3.1.h"

#define MAXMOTORSPEED 100
int x1 = 0;
int y1 = 0;
int x2 = 0;
int y2 = 0;
float num = 0.00;
float FL = 0.00;
float BL = 0.00;
float FR = 0.00;
float BR = 0.00;

void initializeRobot(){
	return;
} // end of initialization

task normalize(){
			if(abs(FL) >= abs(BL) && abs(FL) >= abs(FR) && abs(FL) >= abs(BR) && abs(FL) > 100)
			{
				num = abs(FL / 100);
				FL = FL / num;
				BL = BL / num;
				FR = FR / num;
				BR = BR / num;
			} // end of if
			if(abs(BL) >= abs(FL) && abs(BL) >= abs(FR) && abs(BL) >= abs(BR) && abs(BL) > 100)
			{
				num = abs(BL / 100);
				FL = FL / num;
				BL = BL / num;
				FR = FR / num;
				BR = BR / num;
			} // end of if
			if(abs(FR) >= abs(FL) && abs(FR) >= abs(BL) && abs(FR) >= abs(BR) && abs(FR) > 100)
			{
				num = abs(FR / 100);
				FL = FL / num;
				BL = BL / num;
				FR = FR / num;
				BR = BR / num;
			} // end of if
			if(abs(BR) >= abs(FL) && abs(BR) >= abs(BL) && abs(BR) >= abs(FR) && abs(BR) > 100)
			{
				num = abs(BR / 100);
				FL = FL / num;
				BL = BL / num;
				FR = FR / num;
				BR = BR / num;
			} // end of if
}
task runMotors(){
	//All Motors at 3/5 speed
	FL = FL * 0.60;
	BL = BL * 0.60;
	FR = FR * 0.60;
	BR = BR * 0.60;
	//If back left bumper is pressed, actual speed is increased to 90% of joystick input
	if(joy1Btn(7))
	{
		FL = FL * 1.5;
		BL = BL * 1.5;
		FR = FR * 1.5;
		BR = BR * 1.5;
	}
	//If back right bumper is pressed, actual speed is lowered to 21% of joystick input
	if(joy1Btn(8))
	{
		FL = FL * 0.35;
		BL = BL * 0.35;
		FR = FR * 0.35;
		BR = BR * 0.35;
	}
	motor[motorFL] = FL;
	motor[motorBL] = BL;
	motor[motorFR] = FR;
	motor[motorBR] = BR;
}


task findSpeeds(){
			if((abs(x1) >= 10 || abs(y1) >= 10) && (abs(x2) >= 10))
			{
			FL = x1 + y1 + x2;
			BL = x1 - y1 - x2;
			FR = x1 - y1 + x2;
			BR = x1 + y1 - x2;
			}
			else if(abs(x1) >= 10 || abs(y1) >= 10 || abs(x2) >= 10){
			FL = x1 + y1 + x2;
			BL = x1 - y1 - x2;
			FR = x1 - y1 + x2;
			BR = x1 + y1 - x2;
			}
			else{
			motor[motorFL] = 0;
			motor[motorBL] = 0;
			motor[motorFR] = 0;
			motor[motorBR] = 0;
	}
}

task HoloDrive()
	{

		while(true)
		{
			//Finds the values on the joysticks
			getJoystickSettings(joystick);
			x1 = joystick.joy1_x1;
			y1 = joystick.joy1_y1;
			x2 = joystick.joy1_x2;
			y2 = joystick.joy1_y2;
			//"Convert" the x/y values to motor speeds
			x1 = x1 / 1.28;
			y1 = y1 / 1.28;
			x2 = x2 / 1.28;
			//Determine the values the motors should run at
			startTask(findSpeeds);
			//Normalize the motor speeds
			startTask(normalize);
			//Run the motors
			startTask(runMotors);
			wait1Msec(7);
		}
}

	task main()
	{
		initializeRobot();
		waitForStart();
		startTask(HoloDrive);
		while(true){wait1Msec(1);}
	} // End of task main
